// importing the react and react-dom package
import React from "react";
import ReactDOM from "react-dom"; 
import "./index.css";

// JSX element, header
const welcome = "Welcome to 30 Days of React";
const title = "Getting Started With React";
const subtitle = "JavaScript Library";
const author = {
  firstName: "Siobhan",
  lastName: "Doherty",
}
const date = "May 2, 2023"
const header = (
  <header>
    <div className="header-wrapper">
      <h1>{welcome}</h1>
      <h2>{title}</h2> 
      <h3>{subtitle}</h3>
      <p>Instruction: {author.firstName} {author.lastName}</p> 
      <small>Date: {date}</small>
    </div>
  </header>
)

// JSX element, main 
const main = (
  <main>
    <div className="main-wrapper">
      <p>Prerequisite to get started with {' '}
        <strong>
          <em>React.js</em>
        </strong>
        :
      </p>
      <ul>
        <li>HTML</li>
        <li>CSS</li>
        <li>JavaScript</li>
      </ul>
    </div>
  </main>
)

// JSX element, footer
const footer = (
  <footer>
    <div className="footer-wrapper">
      <p>Copyright 2023</p>
    </div>
  </footer>
)

// JSX element, app, a container or a parent
const app = (
  <div className="app">
    {header}
    {main}
    {footer}
  </div>
)

// render JSX element using ReactDOM package
// ReactDOM has render method and render method takes two arguments
const rootElement = document.getElementById("root");
ReactDOM.render(app, rootElement);
