import React from "react";
import ReactDOM from "react-dom"; 
import asabeneh from "./images/Asabeneh.jpg"; 
import { BsCheckCircleFill } from "react-icons/bs";
import { AiOutlineClockCircle } from "react-icons/ai";

const image = (
  <div>
    <img src={asabeneh} alt="Asabeneh img" />
  </div>
);
const user = (
  <h2>
    ASABENEH YETAYEH <BsCheckCircleFill style={{ backgroundColor: "#fffff", color: "#40E0D0", marginLeft: 10, borderRadius: 10 }} className="bscheckcircle" />
  </h2>
);

const position = "Senior Developer, Finland";

const skillset = [
  "HTML", 
  "CSS", 
  "Sass", 
  "JS", 
  "React", 
  "Redux", 
  "Node", 
  "MongoDB", 
  "Python", 
  "Flask", 
  "Django", 
  "NumPy", 
  "Pandas", 
  "Data Analysis", 
  "MYSQL", 
  "GraphQL", 
  "D3.JS", 
  "Gatsby", 
  "Docker", 
  "Heroku", 
  "Git"
];

const skills = skillset.map((skill, index) => <p key={index}>{skill}</p>);

const date = <p><AiOutlineClockCircle style={{ paddingRight: 10 }} />Joined on Aug 30, 2020</p>;

const ErrorBoundary = class extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }
  static getDerivedStateFromError(error) {
    return { hasError: true };
  }
  render() {
    if (this.state.hasError) {
      return <h1>Something went wrong.</h1>;
    }
    return this.props.children;
  }
}

const app = (
  <ErrorBoundary>
    <div style={{
      display: "flex",
      flexDirection: "column",
      width: "99%",
      height: "100%",
      background: "white",
      borderRadius: 10,
      padding: "20px 20px",
      fontFamily: "Montserrat",
    }}
  >
    <div className="img">{user}</div>
    <div className="text" style={{ display: "flex", flexDirection: "column" }}>
      <p
        style={{
          textTransform: "uppercase",
          display: "flex",
          alignItems: "center",
          marginLeft: 12,
          letterSpacing: 2,
          fontWeight: 1000,
        }}
      >
        {user}{" "}
        <BsCheckCircleFill
          style={{
            background: "#40E0D0",
            color: "white",
            marginLeft: 10,
            borderRadius: 10,
          }}
        />
      </p>
      <p style={{ color: "#73787C", marginLeft: 10 }}>{position}</p>
      <p style={{ marginLeft: 10, fontWeight: 1000 }}>{skills}</p>
      <div style={{ display: "flex", marginLeft: 10 }}>
        {skillset.map((skill) => (
          <span
            key={skill}
            style={{
              padding: 5,
              margin: 10,
              border: "2px solid #40E0D0",
              borderRadius: 10,
              background: "#40E0D0",
              color: "white",
            }}
          >
            {skill}
          </span>
        ))}
      </div>
      <p
        style={{
          marginLeft: 10,
          display: "flex",
          alignItems: "center",
        }}
      >
        <AiOutlineClockCircle style={{ paddingRight: 10 }} />
        {date}
      </p>
    </div>
  </div>
  </ErrorBoundary>
);

const rootElement = document.getElementById("root"); 
ReactDOM.render(app, rootElement);

export default ErrorBoundary;
