import React from "react";
import createRoot from "react-dom/client"; 
import Asabeneh from "./images/Asabeneh.jpg"; 
import CheckIcon from '@mui/icons-material/Check';
import AccessTime from '@mui/icons-material/AccessTime';

const image = (
  <div>
    <img src={Asabeneh} alt="Asabeneh img" />
  </div>
);
const user = (
  <h2>
    ASABENEH YETAYEH <CheckIcon className="done" />
  </h2>
);

const position = "Senior Developer, Finland";

const skillset = [
  "HTML", 
  "CSS", 
  "Sass", 
  "JS", 
  "React", 
  "Redux", 
  "Node", 
  "MongoDB", 
  "Python", 
  "Flask", 
  "Django", 
  "NumPy", 
  "Pandas", 
  "Data Analysis", 
  "MYSQL", 
  "GraphQL", 
  "D3.JS", 
  "Gatsby", 
  "Docker", 
  "Heroku", 
  "Git"
];

const skills = skillset.map((skill, index) => <p key={index}>{skill}</p>);

const date = <p><AccessTime />Joined on Aug 30, 2020</p>;

const ErrorBoundary = class extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }
  static getDerivedStateFromError(error) {
    return { hasError: true };
  }
  render() {
    if (this.state.hasError) {
      return <h1>Something went wrong.</h1>;
    }
    return this.props.children;
  }
}

const app = (
  <ErrorBoundary>
    <div className="app">
      <div className="content">
        {image}
        {user}
        {position}
      </div>
      <h2>SKILLS</h2>
      <div className="skillset">{skills}</div>
      <footer>
        <div>
          {date}
        </div>
      </footer>
    </div>
  </ErrorBoundary>
);

const rootElement = document.getElementById("root"); 
createRoot(app, rootElement);

export default ErrorBoundary;
