import React from "react";
import ReactDOM from "react-dom"; 
import Asabeneh from "./images/Asabeneh.jpg";

const user = {
  avatar: {
    src: Asabeneh,
    alt: "Asabeneh",
    style: { height: "200px", borderRadius: 125, marginLeft: 10 }
  }
};
const name = "Asabeneh Yetayeh"; 
const progression = "Senior Developer, Finland";
const skills = "SKILLS";
const skillset = [
  "HTML", 
  "CSS", 
  "Sass", 
  "JS", 
  "React", 
  "Redux", 
  "Node", 
  "MongoDB", 
  "Python", 
  "Flask", 
  "Django", 
  "NumPy", 
  "Pandas", 
  "Data Analysis", 
  "MYSQL", 
  "GraphQL", 
  "D3.JS", 
  "Gatsby", 
  "Docker", 
  "Heroku", 
  "Git"
];

const date = "Joined on Aug 30, 2020";

const app = (
