  <div style={{
    display: "flex", 
    flexDirection: "column", 
    width: "99%", 
    height: "100%", 
    background: "white", 
    borderRadius: 10, 
    padding: "20px 20px", 
    fontFamily: "Montserrat",
  }}
  >
    <div className="img">{user}</div>
    <div className="text" style={{ display: "flex", flexDirection: "column" }}>
      <p style={{
        textTransform: "uppercase", 
        display: "flex", 
        alignItems: "center", 
        marginLeft: 12, 
        letterSpacing: 2, 
        fontWeight: 1000,
      }}>
        {name}{" "} 
      </p>
      <p style={{ color: "#73787C", marginLeft: 10 }}>{progression}</p>
      <p style={{ marginLeft: 10, fontWeight: 1000 }}>{skills}</p>
      <div style={{ display: "flex", marginLeft: 10 }}>
        {skillset.map((skill) => (
          <span>{skill}</span>
        ))}
      </div>
    </div>
    <p style={{
      marginLeft: 10, 
      display: "flex", 
      alignItems: "center",
    }}>
      {date}
    </p>
  </div>
)

const rootElement = document.getElementById("root");
ReactDOM.render(app, rootElement);
