import React from "react";
import { createRoot } from "react-dom/client"; 
import { createTheme, ThemeProvider } from "@mui/material/styles";
import Asabeneh from "./images/Asabeneh.jpg";
import CheckCircleIcon from "@mui/icons-material/CheckCircle";
import AccessTimeIcon from "@mui/icons-material/AccessTime";

const themeInstance = createTheme();

const user = {
  avatar: {
    src: Asabeneh,
    alt: "Asabeneh",
    style: { height: "200px", borderRadius: 125, marginLeft: 10 },
  },
};

const name = "Asabeneh Yetayeh"; 
const progression = "Senior Developer, Finland";
const skills = "SKILLS";
const skillset = [
  "HTML", 
  "CSS", 
  "Sass", 
  "JS", 
  "React", 
  "Redux", 
  "Node", 
  "MongoDB", 
  "Python", 
  "Flask", 
  "Django", 
  "NumPy", 
  "Pandas", 
  "Data Analysis", 
  "MYSQL", 
  "GraphQL", 
  "D3.JS", 
  "Gatsby", 
  "Docker", 
  "Heroku", 
  "Git"
];

const date = "Joined on Aug 30, 2020";

const app = (
  <ThemeProvider theme={themeInstance}>
    <div 
      style={{
        display: "flex", 
        flexDirection: "column", 
        width: "99%", 
        height: "100%", 
        background: "white", 
        borderRadius: 10, 
        padding: "20px 20px", 
        fontFamily: "Montserrat",
      }}
    >
      <div className="img">
        <img src={user.avatar.src} alt={user.avatar.alt} style={user.avatar.style} />
      </div>
      <div className="text" style={{ display: "flex", flexDirection: "column" }} >
        <p 
          style={{
            textTransform: "uppercase", 
            display: "flex", 
            alignItems: "center", 
            marginLeft: 12, 
            letterSpacing: 2, 
            fontWeight: 1000,
          }}
        >
          {name}{" "} 
          <CheckCircleIcon 
            style={{
              background: "#40E0D0", 
              color: "white", 
              marginLeft: 10, 
              borderRadius: 10,
            }}
          />
        </p>
        <p style={{ color: "#73787C", marginLeft: 10 }}>{progression}</p>
        <p style={{ marginLeft: 10, fontWeight: 1000 }}>{skills}</p>
        <div style={{ display: "flex", marginLeft: 10 }}>
          {skillset.map((skill) => (
            <span 
              key={skill}
              style={{
                padding: 5, 
                margin: 10, 
                border: "2px solid #40E0D0", 
                borderRadius: 10, 
                background: "#40E0D0", 
                color: "white",
              }}
            >
              {skill}
            </span>
          ))}
        </div>
      </div>
      <p style={{ marginLeft: 10, display: "flex", alignItems: "center" }}>
        <AccessTimeIcon style={{ paddingRight: 10 }} />
        {date}
      </p>
    </div>
  </ThemeProvider>
)

const rootElement = document.getElementById("root"); 
const root = createRoot(rootElement);
root.render(app);
