import React from "react";
import { createRoot } from "react-dom/client"; 
import Asabeneh from "./images/Asabeneh.jpg"; 
import { BsCheckCircleFill } from "react-icons/bs";
import { AiOutlineClockCircle } from "react-icons/ai";

const image = (
  <div>
    <img src={Asabeneh} alt="Asabeneh img" />
  </div>
);
const user = (
  <h2>
    ASABENEH YETAYEH <BsCheckCircleFill className="bscheckcircle" />
  </h2>
);

const position = "Senior Developer, Finland";
// const skills = "SKILLS";
const skillset = [
  "HTML", 
  "CSS", 
  "Sass", 
  "JS", 
  "React", 
  "Redux", 
  "Node", 
  "MongoDB", 
  "Python", 
  "Flask", 
  "Django", 
  "NumPy", 
  "Pandas", 
  "Data Analysis", 
  "MYSQL", 
  "GraphQL", 
  "D3.JS", 
  "Gatsby", 
  "Docker", 
  "Heroku", 
  "Git"
];

const skills = skillset.map((skill) => <p>{skill}</p>);

const date = <p><AiOutlineClockCircle />Joined on Aug 30, 2020</p>;

const app = (
  <div 
    style={{
      display: "flex", 
      flexDirection: "column", 
      width: "99%", 
      height: "100%", 
      background: "white", 
      borderRadius: 10, 
      padding: "20px 20px", 
      fontFamily: "Montserrat",
    }}
  >
    <div className="img">
      <img src={user.avatar.src} alt={user.avatar.alt} style={user.avatar.style} />
    </div>
    <div className="text" style={{ display: "flex", flexDirection: "column" }} >
      <p 
        style={{
          textTransform: "uppercase", 
          display: "flex", 
          alignItems: "center", 
          marginLeft: 12, 
          letterSpacing: 2, 
          fontWeight: 1000,
        }}
      >
        {name}{" "} 
      </p>
      <p style={{ color: "#73787C", marginLeft: 10 }}>{position}</p>
      <p style={{ marginLeft: 10, fontWeight: 1000 }}>{skills}</p>
      <div style={{ display: "flex", marginLeft: 10 }}>
        {skillset.map((skill) => (
          <span 
            key={skill}
            style={{
              padding: 5, 
              margin: 10, 
              border: "2px solid #40E0D0", 
              borderRadius: 10, 
              background: "#40E0D0", 
              color: "white",
            }}
          >
            {skill}
          </span>
        ))}
      </div>
    </div>
    <p style={{ marginLeft: 10, display: "flex", alignItems: "center" }}>
      {date}
    </p>
  </div>
)

const rootElement = document.getElementById("root"); 
const root = createRoot(rootElement);
root.render(app);
